using GSMA.MobileConnect.Utils;
using NUnit.Framework;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace GSMA.MobileConnect.Test.Discovery
{
    [TestFixture]
    public class DiscoveryRepsonseTests
    {
        [Test]
        public void CachedDiscoveryResponseShouldClearSubscriberId()
        {
            var responseJson = "{\"ttl\":1461169322705,\"subscriber_id\":\"\",\"response\":{\"serving_operator\":\"Example Operator A\",\"country\":\"US\",\"currency\":\"USD\",\"apis\":{\"operatorid\":{\"link\":[{\"href\":\"http://operator_a.sandbox2.mobileconnect.io/oidc/authorize\",\"rel\":\"authorization\"},{\"href\":\"http://operator_a.sandbox2.mobileconnect.io/oidc/accesstoken\",\"rel\":\"token\"},{\"href\":\"http://operator_a.sandbox2.mobileconnect.io/oidc/userinfo\",\"rel\":\"userinfo\"},{\"href\":\"openid profile email\",\"rel\":\"scope\"}]}},\"client_id\":\"66742a85-2282-4747-881d-ed5b7bd74d2d\",\"client_secret\":\"f15199f4-b658-4e58-8bb3-e40998873392\",\"subscriber_id\":\"\"}}";
            var restResponse = new RestResponse(System.Net.HttpStatusCode.OK, responseJson);
            var initialDiscoveryResponse = new MobileConnect.Discovery.DiscoveryResponse(restResponse);

            var cachedResponse = new MobileConnect.Discovery.DiscoveryResponse(initialDiscoveryResponse);

            Assert.IsNotNull(initialDiscoveryResponse.ResponseData.subscriber_id);
            Assert.IsNull(cachedResponse.ResponseData.subscriber_id);
        }

        [Test]
        public void CachedDiscoveryResponseShouldMaintainOperatorUrls()
        {
            var responseJson = "{\"ttl\":1461169322705,\"subscriber_id\":\"\",\"response\":{\"serving_operator\":\"Example Operator A\",\"country\":\"US\",\"currency\":\"USD\",\"apis\":{\"operatorid\":{\"link\":[{\"href\":\"http://operator_a.sandbox2.mobileconnect.io/oidc/authorize\",\"rel\":\"authorization\"},{\"href\":\"http://operator_a.sandbox2.mobileconnect.io/oidc/accesstoken\",\"rel\":\"token\"},{\"href\":\"http://operator_a.sandbox2.mobileconnect.io/oidc/userinfo\",\"rel\":\"userinfo\"},{\"href\":\"openid profile email\",\"rel\":\"scope\"}]}},\"client_id\":\"66742a85-2282-4747-881d-ed5b7bd74d2d\",\"client_secret\":\"f15199f4-b658-4e58-8bb3-e40998873392\",\"subscriber_id\":\"\"}}";
            var restResponse = new RestResponse(System.Net.HttpStatusCode.OK, responseJson);
            var initialDiscoveryResponse = new MobileConnect.Discovery.DiscoveryResponse(restResponse);

            var cachedResponse = new MobileConnect.Discovery.DiscoveryResponse(initialDiscoveryResponse);

            Assert.AreEqual(initialDiscoveryResponse.OperatorUrls, cachedResponse.OperatorUrls);
        }
    }
}
